package io.github.zackyl.recyclerview;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.widget.RelativeLayout;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {
    private RecyclerView contactsRecyclerView;
    private RelativeLayout relativeLayout;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        contactsRecyclerView = findViewById(R.id.contactsRecyclerView);
        ArrayList<Contact> contacts = new ArrayList<>();
        contacts.add(new Contact("Zack",  "Zack@gmail.com", "data:image/jpeg;base64,"));
        contacts.add(new Contact("Bob Marley",  "Bob@gmail.com", "https://stockphoto.com/assets/landingpage/images/Depositphotos_9407624_original.jpg"));
        contacts.add(new Contact("KEK",  "Kek@gmail.com", "https://www.shutterstock.com/shutterstock/photos/1712082700/display_1500/stock-photo-attractive-african-young-confident-businesswoman-sitting-at-the-office-table-with-group-of-1712082700.jpg"));
        contacts.add(new Contact("Amy Brown",  "Amy.Brown@gmail.com", "https://stockphoto.com/assets/landingpage/images/Depositphotos_46360455_original.jpg"));
        contacts.add(new Contact("Emma Watson",  "Emma.Watson@gmail.com", "https://upload.wikimedia.org/wikipedia/commons/7/7f/Emma_Watson_2013.jpg"));

        ContactsRecyclerViewAdapter adapter = new ContactsRecyclerViewAdapter(this);
        adapter.setContacts(contacts);
        contactsRecyclerView.setAdapter(adapter);
        contactsRecyclerView.setLayoutManager(new GridLayoutManager(this, 2));
    }
}